import React, { useState } from 'react'
import './UserForm.css';



function AddUser({ onSubmit }) {

    const [formData, setFormData] = useState({
        name: '',
        cta: '',
        spoc: '',
        mobile: '',
        email: '',
      });
      const [action, setAction] = useState('Add User');

      const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
      };

      const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(formData, action);
      };

  return (
    <>
    <h1 className="heading" >ADD User Form</h1>
    <p className="notice"> * Name and Mobile are Required fields</p>
    <div className="user-form">

      <form onSubmit={handleSubmit}>
        <label   style={{color:"red"}}>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            required={true}
            onChange={handleInputChange}
          />
        </label>
        <label>
          CTA:
          <input
            type="text"
            name="cta"
            value={formData.cta}
            onChange={handleInputChange}
          />
        </label>
        <label>
          SPOC:
          <input
            type="text"
            name="spoc"
            value={formData.spoc}
            onChange={handleInputChange}
          />
        </label>
        <label style={{color:"red"}}>
          Mobile:
          <input
            type="text"
            name="mobile"
            value={formData.mobile}
            required={true}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Email:
          <input
            type="text"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Action:
          <select name="action" onChange={(e) => setAction(e.target.value)}>
            <option value="Add User">Add User</option>
            <option value="Update User">Update User</option>
          </select>
        </label>
        <button className="dbutton" type="submit">Submit</button>
      </form>
    </div>
    </>
  )
}

export default AddUser;
