import React, { Component } from 'react';

class UserList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userList: [],
    };
  }

  componentDidMount() {
    // Use fetch API or axios to make an AJAX request to your API endpoint
    fetch('/https://qa2.sunbasedata.com/sunbase/portal/api/assignment.jsp?cmd=get_customer_list') // Replace with your API endpoint URL
      .then((response) => response.json())
      .then((data) => {
        this.setState({ userList: data });
      })
      .catch((error) => {
        console.error('API request failed:', error);
      });
  }

  deleteUser(uuid) {
    // Make an AJAX request to the /delUser endpoint with the UUID
    fetch('/delUser?uuid=' + uuid, {
      method: 'POST',
    })
      .then(() => {
        // Handle success (e.g., remove the row from the state)
        this.setState((prevState) => ({
          userList: prevState.userList.filter((user) => user.uuid !== uuid),
        }));
      })
      .catch((error) => {
        console.error('Delete request failed:', error);
      });
  }

  editUser(uuid) {
    // Redirect to the edit endpoint with the UUID
    window.location.href = '/edit?uuid=' + uuid; // Replace with your edit endpoint URL
  }

  render() {
    return (
      <div>
        <h1>User Lists</h1>
        <div style={{ textAlign: 'center' }}>
          {/* Add a "Create" button that links to the /create endpoint */}
          <a href="/create">
            <button style={{ backgroundColor: '#007BFF', color: 'white' }}>
              Create
            </button>
          </a>
        </div>

        <table className="user-table">
          <thead>
            <tr>
              <th>uuid</th>
              <th>first_name</th>
              <th>last_name</th>
              <th>street</th>
              <th>address</th>
              <th>city</th>
              <th>state</th>
              <th>email</th>
              <th>phone</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {this.state.userList.map((user) => (
              <tr key={user.uuid}>
                <td>{user.uuid}</td>
                <td>{user.first_name}</td>
                <td>{user.last_name}</td>
                <td>{user.street}</td>
                <td>{user.address}</td>
                <td>{user.city}</td>
                <td>{user.state}</td>
                <td>{user.email}</td>
                <td>{user.phone}</td>
                <td className="actions">
                  <span
                    className="edit-button"
                    onClick={() => this.editUser(user.uuid)}
                  >
                    Edit
                  </span>
                </td>
                <td className="actions">
                  <span
                    className="delete-button"
                    onClick={() => this.deleteUser(user.uuid)}
                  >
                    Delete
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default UserList;
