import React, { Component } from 'react';

class UserList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userList: [],
    };
  }

  async componentDidMount() {
    try {
      const response = await fetch('/getClientLists'); // Replace with your API endpoint URL
      if (!response.ok) {
        throw new Error('API request failed');
      }
      const data = await response.json();
      this.setState({ userList: data });
    } catch (error) {
      console.error('API request failed: While Getting the ClientList', error);
    }
  }


  deleteUser(uuid) {
    // Make an AJAX request to the /delUser endpoint with the UUID
    fetch('/delUser?uuid=' + uuid, {
      method: 'POST',
    })
      .then(() => {
        // Handle success (e.g., remove the row from the state)
        this.setState((prevState) => ({
          userList: prevState.userList.filter((user) => user.uuid !== uuid),
        }));
      })
      .catch((error) => {
        console.error('Delete request failed:', error);
      });
  }

  editUser(uuid) {
    // Redirect to the edit endpoint with the UUID
    window.location.href = '/edit?uuid=' + uuid; // Replace with your edit endpoint URL
  }

  render() {
    return (
      <div>
        <h1>User Lists</h1>
        <div style={{ textAlign: 'center' }}>
          {/* Add a "Create" button that links to the /create endpoint */}
          <a href="/create">
            <button style={{ backgroundColor: '#007BFF', color: 'white' }}>
              Create
            </button>
          </a>
        </div>

        <table className="user-table">
          <thead>
            <tr>
              <th>Contact</th>
              <th>CTA</th>
              <th>spoc</th>
              <th>Mobile</th>
              <th>Email</th>
              <th>CreatedDate</th>
            </tr>
          </thead>
          <tbody>
            {this.state.userList.map((user) => (
              <tr key={user.uuid}>
                <td>{user.uuid}</td>
                <td>{user.first_name}</td>
                <td>{user.last_name}</td>
                <td>{user.street}</td>
                <td>{user.address}</td>
                <td>{user.address}</td>
                <td className="actions">
                  <span
                    className="edit-button"
                    onClick={() => this.editUser(user.uuid)}
                  >
                    Edit
                  </span>
                </td>
                <td className="actions">
                  <span
                    className="delete-button"
                    onClick={() => this.deleteUser(user.uuid)}
                  >
                    Delete
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default UserList;
