import React, { useState, useEffect } from 'react';

function UserList() {

  //Using the State to set the UserList from the API
  const [userList, setUserList] = useState([]);

  useEffect(() => {
    const getUserList = async () => {
      try {
        const response = await fetch('/getClientLists', {
          method: 'GET', 
        });
        if (!response.ok) {
          throw new Error('API request failed');
        }
        const data = await response.json();
        setUserList(data);
      } catch (error) {
        console.error('API request failed: While Getting the ClientList', error);
      }
    };
    getUserList();
  }, []);  // Insering Empty dependency array to ensure that useEffect runs only once and not Making any unnecessary requests to API



  //Keeping the track of the Deleted User list so that /getClientLists Endpoint should not be hit over and over again
  const deleteUser = async (phoneNo) => {
    try {

      // Making a POST request to the API to delete the user via API, treating phone number as a unique identifier
      // Using a parameterized and POST request to hit the API
      // Using parameterized query to increase the performance and keeping it readable as the data is not sensitive
      // Processing the data by using filteration at client side saves API from too many requests.

      const response = await fetch('/deleteUser?phoneNo=' + phoneNo, {
        method: 'POST',
      });

      if (response.ok) {
        // Handle success (e.g., remove the row from the state)
        setUserList((userList) =>
        userList.filter((user) => user.phoneNo !== phoneNo)
        );
      } else {
        throw new Error('Delete request failed');
      }
    } catch (error) {
      console.error('Delete request failed:', error);
    }
  };


  const editUser = (phoneNo) => {
    // Redirect to the edit endpoint with the UUID
    window.location.href = '/edit?phone=' + phoneNo; // Replace with your edit endpoint URL
  };

  return (
    <div>
      <h1>User Lists</h1>
      <div style={{ textAlign: 'center' }}>
        {/* Add a "Create" button that links to the /create endpoint */}
        <a href="/create">
          <button style={{ backgroundColor: '#007BFF', color: 'white' }}>
            Create
          </button>
        </a>
      </div>

      <table className="user-table">
        <thead>
          <tr>
            <th>Contact</th>
            <th>CTA</th>
            <th>spoc</th>
            <th>Mobile</th>
            <th>Email</th>
            <th>CreatedDate</th>
          </tr>
        </thead>
        <tbody>
          {userList.map((user) => (
            <tr key={user.uuid}>
              <td>{user.uuid}</td>
              <td>{user.first_name}</td>
              <td>{user.last_name}</td>
              <td>{user.street}</td>
              <td>{user.address}</td>
              <td>{user.address}</td>
              <td className="actions">
                <span className="edit-button" onClick={() => editUser(user.uuid)}>
                  Edit
                </span>
              </td>
              <td className="actions">
                <span
                  className="delete-button"
                  onClick={() => deleteUser(user.uuid)}
                >
                  Delete
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default UserList;
