import React, { useState } from 'react'
import './AddUpateUser.css';



export default function AddUpateUser({ onSubmit }) {

    const [formData, setFormData] = useState({
        name: '',
        cta: '',
        spoc: '',
        mobile: '',
        email: '',
      });
      const [action, setAction] = useState('Add User');

      const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
      };

      const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(formData, action);
      };

  return (
    <div className="user-form">
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
          />
        </label>
        <label>
          CTA:
          <input
            type="text"
            name="cta"
            value={formData.cta}
            onChange={handleInputChange}
          />
        </label>
        <label>
          SPOC:
          <input
            type="text"
            name="spoc"
            value={formData.spoc}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Mobile:
          <input
            type="text"
            name="mobile"
            value={formData.mobile}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Email:
          <input
            type="text"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Action:
          <select name="action" onChange={(e) => setAction(e.target.value)}>
            <option value="Add User">Add User</option>
            <option value="Update User">Update User</option>
          </select>
        </label>
        <button type="submit">Submit</button>
      </form>
    </div>
  )
}
