import React, { useState, useEffect } from 'react';

function UserList() {
  const [userList, setUserList] = useState([]);

  useEffect(() => {
    const getUserList = async () => {
      try {
        const response = await fetch('/getClientLists');
        if (!response.ok) {
          throw new Error('API request failed');
        }
        const data = await response.json();
        setUserList(data);
      } catch (error) {
        console.error('API request failed: While Getting the ClientList', error);
      }
    };

    getUserList();
  }, []); // Empty dependency array ensures useEffect runs only once, like componentDidMount

  const deleteUser = (uuid) => {
    // Make an AJAX request to the /delUser endpoint with the UUID
    fetch('/delUser?uuid=' + uuid, {
      method: 'POST',
    })
      .then(() => {
        // Handle success (e.g., remove the row from the state)
        setUserList((prevUserList) =>
          prevUserList.filter((user) => user.uuid !== uuid)
        );
      })
      .catch((error) => {
        console.error('Delete request failed:', error);
      });
  };

  const editUser = (uuid) => {
    // Redirect to the edit endpoint with the UUID
    window.location.href = '/edit?uuid=' + uuid; // Replace with your edit endpoint URL
  };

  return (
    <div>
      <h1>User Lists</h1>
      <div style={{ textAlign: 'center' }}>
        {/* Add a "Create" button that links to the /create endpoint */}
        <a href="/create">
          <button style={{ backgroundColor: '#007BFF', color: 'white' }}>
            Create
          </button>
        </a>
      </div>

      <table className="user-table">
        <thead>
          <tr>
            <th>Contact</th>
            <th>CTA</th>
            <th>spoc</th>
            <th>Mobile</th>
            <th>Email</th>
            <th>CreatedDate</th>
          </tr>
        </thead>
        <tbody>
          {userList.map((user) => (
            <tr key={user.uuid}>
              <td>{user.uuid}</td>
              <td>{user.first_name}</td>
              <td>{user.last_name}</td>
              <td>{user.street}</td>
              <td>{user.address}</td>
              <td>{user.address}</td>
              <td className="actions">
                <span className="edit-button" onClick={() => editUser(user.uuid)}>
                  Edit
                </span>
              </td>
              <td className="actions">
                <span
                  className="delete-button"
                  onClick={() => deleteUser(user.uuid)}
                >
                  Delete
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default UserList;
